light.stylesheet:
QWidget { background: #ffffff; color: #000000; }
QTextEdit { background: #b3b3b3; color: #000000; border: 2px solid #ccc; }
QMenuBar, QMenu, QMenu::item { background: #f7f7f7; color: #000000; }
QMenuBar::item:selected { background: #e0e0e0; color: #000000; }
QMenu::item:selected { background: #e0e0e0; color: #000000; }
QPushButton { background: #eaeaea; color: #000000; }
QComboBox { background: #ffffff; color: #000000; border: 1px solid #ccc; }
QComboBox QAbstractItemView { background: #ffffff; color: #000000; selection-background-color: #e0e0e0; selection-color: #000000; }

dark.stylesheet:
QWidget { background: #282828; color: #eaeaea; }
QTextEdit { background: #404040; color: #eaeaea; border: 2px solid #333; }
QMenuBar, QMenu, QMenu::item { background: #1b1b1b; color: #eaeaea; }
QMenuBar::item:selected { background: #333333; color: #ffffff; }
QMenu::item:selected { background: #444444; color: #ffffff; }
QPushButton { background: #2b2b2b; color: #eaeaea; }
QComboBox { background: #1e1e1e; color: #eaeaea; border: 1px solid #444; }
QComboBox QAbstractItemView { background: #1e1e1e; color: #eaeaea; selection-background-color: #333333; selection-color: #ffffff; }
